package CoreJava;

public class C_NestedLoops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=1;i<=4;i++) {
			System.out.println("Outer loop started");
			for(int j=1;j<=4;j++) {
				System.out.println("Inner Loop");
			}
			System.out.println("Outer loop finished");
		}

	}

}
